__RAW__# Update a specific node (e.g., change text at node 0.2.1)
curl -X POST http://yoursite.local/management/editStructure \
  -H "Authorization: Bearer YOUR_TOKEN" \
  -H "Content-Type: application/json" \
  -d '{
    "type": "page",
    "name": "home",
    "nodeId": "0.2.1",
    "structure": {"textKey": "__RAW__New text content"}
  }'

__RAW__# Insert after a node
{"type": "page", "name": "home", "nodeId": "0.2", "action": "insertAfter",
 "structure": {"tag": "p", "children": [{"textKey": "new.paragraph"}]}}

# Delete a node
{"type": "page", "name": "home", "nodeId": "0.3", "action": "delete"}

# Insert before a node
{"type": "page", "name": "home", "nodeId": "0.1", "action": "insertBefore",
 "structure": {"tag": "div", "params": {"class": "banner"}}}

__RAW__# Add/update translation keys (safe merge - won't delete existing)
curl -X POST http://yoursite.local/management/setTranslationKeys \
  -H "Authorization: Bearer YOUR_TOKEN" \
  -H "Content-Type: application/json" \
  -d '{
    "language": "en",
    "translations": {
      "about": {
        "title": "About Us",
        "description": "Learn more about our company"
      }
    }
  }'

__RAW__# Change site colors instantly
curl -X POST http://yoursite.local/management/setRootVariables \
  -H "Authorization: Bearer YOUR_TOKEN" \
  -H "Content-Type: application/json" \
  -d '{
    "variables": {
      "--color-primary": "#6366f1",
      "--color-secondary": "#ec4899",
      "--spacing-lg": "2rem"
    }
  }'

__RAW__# Add a new CSS rule
curl -X POST http://yoursite.local/management/setStyleRule \
  -H "Authorization: Bearer YOUR_TOKEN" \
  -H "Content-Type: application/json" \
  -d '{
    "selector": ".hero-banner",
    "styles": {
      "background": "linear-gradient(135deg, var(--color-primary), var(--color-secondary))",
      "padding": "4rem 2rem",
      "text-align": "center"
    }
  }'

__RAW__# Create a fade-in animation
curl -X PATCH http://yoursite.local/management/setKeyframes \
  -H "Authorization: Bearer YOUR_TOKEN" \
  -H "Content-Type: application/json" \
  -d '{
    "name": "fadeInUp",
    "frames": {
      "from": "opacity: 0; transform: translateY(20px);",
      "to": "opacity: 1; transform: translateY(0);"
    },
    "allowOverwrite": true
  }'
